package de.wisag.automation.micas.ui.labeling;

import org.eclipse.emf.edit.ui.provider.AdapterFactoryLabelProvider;
import org.eclipse.xtext.ui.label.DefaultEObjectLabelProvider;

import com.google.inject.Inject;

import de.wisag.automation.micas.mIC.Task;

/**
 * Provides labels for a EObjects. see
 * http://www.eclipse.org/Xtext/documentation/latest/xtext.html#labelProvider
 */
public class MICLabelProvider extends DefaultEObjectLabelProvider {

    @Inject
    public MICLabelProvider(AdapterFactoryLabelProvider delegate) {
        super(delegate);
    }

    @Override
    public Object text(Object element) {
        if (element instanceof Task) {
            Task task = (Task) element;
            return String.format("Task : %d, %s", task.getIndex(), task.getType());
        }
        return super.text(element);
    }
    /*
     * //Labels and icons can be computed like this:
     *
     * String text(MyModel ele) { return "my "+ele.getName(); }
     *
     * String image(MyModel ele) { return "MyModel.gif"; }
     */
}
